func longestArithSeqLength(nums []int) int {
	diffs := make([][]int, len(nums))
	for i := 0; i < len(nums); i++ {
		diffs[i] = make([]int, 1001)
	}
	maxCount := 0
	for i := 1; i < len(nums); i++ {
		for k := 0; k < i; k++ {
			diff := nums[i] - nums[k] + 500
			diffs[i][diff] = max(diffs[i][diff], diffs[k][diff]+1)
			maxCount = max(maxCount, diffs[i][diff])
		}
	}
	return maxCount + 1
}
func max(a, b int) int {
	if a > b {
		return a
	} else {
		return b
	}
}
